const rotateRight = (head, k) => {
	if (!head) return null;

	let tail = head;
	let len = 1; // 1-index based

	// get tail node and length of LL
	while (tail.next) {
		tail = tail.next;
		len++;
	}

	tail.next = head; // create cycle: connect to head
	len = len - (k % len);

	// if len === k: rotates to original LL
	if (len === 0) return head;

	// find point of reverse
	while (len--) {
		tail = tail.next;
	}

	head = tail.next; // set as new head
	tail.next = null; // server connection

	return head;
};

