class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {

		// if linked list is NULL or k is 0 than return head
        if(k == 0 or head == NULL) return head;

		// first find the size of the linked list, and point tail at the end of the linked list
        int size = 0;
        ListNode* tail = new ListNode(0, head);
        while(tail->next)
        {
            tail = tail->next;
            size++;
        }

		// now if the value of k is greater than size than update it, as after this we need not to rotate list more than size time
        k %= size;

		// if k is zero than return head as we need not to rotate the list
        if(k == 0) return head;

		// now we need to rotate the linked list so we point our linked list tail to head
        tail -> next = head;

		// subtract k from the size to find the rotation point from the start and make it head and its tail equal to NULL
        k = size - k;
        ListNode* temp = new ListNode(0, head);
        while(k--)
        {
            temp = temp->next;
        }
        head = temp->next;
        temp->next = NULL;

		// return the new head
        return head;
    }
};
