```swift
class Solution {
    func rotateRight(_ head: ListNode?, _ k: Int) -> ListNode? {
        if head == nil { return nil }

        var prev = head, post = head

        var len: Int {
            get {
                var value = 0
                var dummy = head
                while let node = dummy {
                    dummy = node.next
                    value += 1
                }
                return value
            }
        }

        var places = (k % len)

        while places > 0 {
            post = post?.next
            places -= 1
        }

        while let postnext = post?.next {
            post = postnext
            prev = prev?.next
        }

        post?.next = head
        post = prev?.next
        prev?.next = nil

        return post
    }
}
