```

struct ListNode* rotateRight(struct ListNode* head, int k)
{
        struct ListNode* iter = head, *prev, *iter2;

        // if list needs no rotation or head is null (checking these conditions makes the solution more efficient)
        if (k == 0 || !head)
			return head;

        // counting number of nodes in list
        size_t num_nodes = 0;
        while (iter)
        {
            iter = iter->next;
            num_nodes++;
        }

        // if there is one node in list or after rotation the list will be same as given list then there is no need to rotate list 
	    // (checking these conditions makes the solution more efficient)
        if (num_nodes == 1 || k % num_nodes == 0)
			return head;

        // rotating:

        iter = head;
        k %= num_nodes;
		// find node that will be head
        while (num_nodes - k)
        {
            prev = iter;
            iter = iter->next;
            num_nodes--;
        }
        prev->next = NULL;
		// attach the previous tail to the previous head
        iter2 = iter;

        while (iter2->next)
			iter2 = iter2->next;

        iter2->next = head;
        head = iter;

        return head;
}
