/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {
        if(head==NULL || head->next == NULL ||k==0)
     {
          return head ;
     }

     //Compute the length of the linked list 
     ListNode* cur = head ;
     int len = 1;
     while(cur->next!=NULL)
     {
          ++len ;
          cur = cur->next ;
     }

     //Pointing the last node to the head;
     cur->next = head ;
     k = k % len ;//Like if we have 12 rotation and k = 5 then till 10 rotation its same as the original linked list , so we do this .
     k = len - k ; // So that we can able to know how much we have to traverse from the head .
     while(k--)
     {
          cur = cur->next ;
     }

     //Make the node to the head and break the connection
     head = cur->next ;
     cur->next = NULL ;


     return head;

    }
}; 
