``` java
class Solution {
    public int maxProfit(int[] prices) {
        int len = prices.length;
        if (len == 0) return 0;
        int preMin = prices[0];
        int maxPro = 0;

        for (int i = 1; i < len; i++){
            int cur = prices[i];
            maxPro = Math.max(maxPro, cur - preMin);
            preMin = Math.min(preMin, cur);
        }
        return maxPro;
    }
}
