#define vi vector<int>

class Solution {
public:
    int maxProfit(vector<int>& prices) {
        int size = prices.size(), res = 0;
        if(!size) return 0;
        vi dp(2, 0);
        dp[0] = prices[0];
        for(int i = 1; i < size; ++i) {
            res = max(res, prices[i]-dp[0]);
            dp[1] = min(dp[0], prices[i]);
            swap(dp[0], dp[1]);
        }
        return res;
    }
};

