class Solution {
    public int maxProfit(int[] prices) {
        int max = 0;
        int delta = 0;
        for (int i = prices.length-1; i >= 0; i--) {
            if (prices[i] >= max) {
                max = prices[i];
            } else {
                delta = Math.max(max - prices[i], delta);
            }
        }
        return delta;
    }
}

