            for (int j = i + 1; j < prices.length; j++) {
                main_profit = prices[j] - prices[i];

                if (profit < main_profit) {
                    profit = main_profit;
                }
            }
        }

        if (profit <= 0) {
            profit = 0;
        }

        return profit;
    }
