class Solution {
public:
    int maxProfit(vector<int>& prices) {
        vector<int>v=prices;
        int count=0;
        sort(v.begin(),v.end(),greater<int>());
        for(int i=0;i<v.size();i++){
            if(v[i]==prices[i])count++;
        }
        if(count==v.size())return 0;
        vector<int>G(prices.size()*prices.size());
        for(int i=prices.size()-1;i>0;i--){
            for(int j=i-1;j>=0;j--){
                int t_dex=prices[i]-prices[j];
                G.push_back(t_dex);
            }
        }
        sort(G.begin(),G.end(),greater<int>());
        for(int i=0;i<G.size();i++){
            if(G[i]>0)return G[i];
        }
        return 0;
    }
};

