                auto node = stk.top();
                stk.pop();
                int execTime = stoi(res[2]) - node.second + 1;
                result[node.first] += execTime;
                if (stk.size() > 0) {
                    result[stk.top().first] -= execTime;
                }
            }
        }
        return result;
    }
