class Solution {
public:

    tuple<int, bool, int> parseEntry(string const& entry)
    {
        // Vector of string to save tokens
        vector <string> tokens;

        // stringstream class check1
        stringstream check1(entry);

        string intermediate;

        // Tokenizing w.r.t. colom ':'
        while(getline(check1, intermediate, ':'))
        {
            tokens.push_back(intermediate);
        }

        return {stoi(tokens[0]), tokens[1]=="start", stoi(tokens[2])};
    }

    vector<int> exclusiveTime(int n, vector<string>& logs) {
        vector<int> times(n, 0);

        int functionID, time;
        bool isStart;
        tie(functionID, isStart, time) = parseEntry(logs[0]);
        assert(isStart);
        stack<pair<int, int>> functionStack;
        functionStack.push({functionID, time});

        for(int i = 1; i < logs.size(); ++i)
        {
            auto const& entry = logs[i];
            tie(functionID, isStart, time) = parseEntry(entry);
            if(isStart)
            {
                if(not functionStack.empty())
                {
                    /*
                    Current function called another one, let's stop this and account 
                    for its time
                    */
                    times[functionStack.top().first] += time - functionStack.top().second;
                }
                functionStack.push({functionID, time});
            }
            else
            {
                assert(not functionStack.empty());
                assert(functionID == functionStack.top().first);

                /*
                These + 1 are a little tricky: the current function will end at the end
                of the time in the entry, not the beginning, so we must account for that
                extra second in its running time, hence the + 1
                */
                times[functionID] += time - functionStack.top().second + 1;
                functionStack.pop();

                /*
                The previous function in the callstack has resumed working, let's update 
                its begin time. Same as before, the next function in the stack will resume
                working in the second NEXT than that specified in the entry, since in that
                second the previous function will still be working
                */
                if(not functionStack.empty())
                    functionStack.top().second = time+1;
            }
        }
        return times;
    }
};
```

