class Log:

    def __init__(self, log: str):
        splitLog = log.split(':')
        self.id = int(splitLog[0])
        self.isStart = splitLog[1] == 'start'
        self.time = int(splitLog[2])

class Solution:

    def exclusiveTime(self, n: int, logs: List[str]) -> List[int]:
        wrappedLogs = [Log(log) for log in logs]        
        idToElapsed = [0] * (max([log.id for log in wrappedLogs]) + 1)
        logStack = []

        for log in wrappedLogs:
            if log.isStart:
                logStack.append(log)
            else:
                previousLog = logStack.pop()
                timeDifference = log.time - previousLog.time + 1

                idToElapsed[previousLog.id] += timeDifference

                if logStack:
                    idToElapsed[logStack[-1].id] -= timeDifference

        return idToElapsed
