class Solution:
    def exclusiveTime(self, n: int, logs: List[str]) -> List[int]:
        stack, output = [], [0] * n

        normalize = lambda k, e, t : \
            [int(k), e, int(t) + (1 if e == 'end' else 0)]

        stack_ops = {   'start' : lambda k : stack.append(k), \
                        'end' : lambda k : stack.pop(), }

        for log in logs:
            k, e, t = normalize(*log.split(':'))
            if stack:
                output[stack[-1]] += t - last_t
            stack_ops[e](k)
            last_t = t
        return output
