class Solution {
    enum Event {kStart, kEnd};
    vector<int> ans;
    tuple<int, enum Event, int> parseLog(const string &log)
    {
        tuple<int, enum Event, int> ret;
        int pos = log.find(':');
        get<0>(ret) = stoi(log.substr(0, pos));
        int prev = pos; pos = log.find(':', prev+1);

        get<1>(ret) = (log.substr(prev+1, pos-prev-1) == "start") ? kStart : kEnd;
        get<2>(ret) = stoi(log.substr(pos+1));
        return ret;
    }
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) {
        ans = vector<int>(n, 0);
        stack<pair<int, int> > s;
        stack<pair<int, int> > res;
        int level = 0;

        for (int i = 0; i < logs.size(); i++)
        {
            auto[fid, event, timestamp] = parseLog(logs[i]);
            if (event == kStart)
            {
                level++;
                s.push({fid, timestamp});
            }
            else 
            {
                level--;
                pair<int, int> top = s.top();
                ans[top.first] += timestamp-top.second+1;
                int temp = timestamp-top.second+1;
                s.pop();
                while (!res.empty() && res.top().second > level)
                {
                    ans[top.first] -= res.top().first;
                    res.pop();
                }
                res.push({temp, level});
            }
        }
        return ans;
    }
};
