class Solution {
public:

    vector<int> exclusiveTime(int n, vector<string>& logs) {
		// pair is implemented as : pair<id, pair<tstamp, extra>>
        stack<pair<int, pair<int, int>>> st;
        vector<int> exclusive(n, 0);

        for(int i=0; i<logs.size(); ++i){
            string log = logs[i];

            int f = log.find(':');
            int l = log.rfind(':');

            int id = stoi(log.substr(0, f));
            char c = log[f+1];
            int tstamp = stoi(log.substr(l+1));

            if(c == 's'){
                st.push({id, {tstamp, 0}});
            } else {
                int time = tstamp - st.top().second.first + 1 - st.top().second.second;
                exclusive[id] += time;
                int extra = st.top().second.second; // extra stores extra time of each log
                st.pop();
                if(!st.empty()) st.top().second.second += time + extra;
            }
        }

        return exclusive;
    }
};

