class Solution {
    public int[] exclusiveTime(int n, List<String> logs) {
        int a[] = new int[n];
        Stack<Integer> stack = new Stack<>();
         Stack<Integer> sstack = new Stack<>();
        boolean flag = false;
        for(String s: logs) {
            int aa = s.indexOf(':');
            int b = s.lastIndexOf(':');
            int id = Integer.parseInt(s.substring(0,aa));
            char status = s.charAt(aa+1);
            int duration = Integer.parseInt(s.substring(b+1));
            if(status == 's') {
                if(!stack.isEmpty()) {
                    if(flag)
                        a[stack.peek()] += duration - sstack.peek()-1;
                    else
                        a[stack.peek()] += duration - sstack.peek();
                }
                stack.push(id);
                sstack.push(duration);
                flag = false;
            }
            else {
                if(flag)
                    a[stack.peek()] += duration - sstack.pop();
                else
                    a[stack.peek()] += duration - sstack.pop() + 1;
                stack.pop();
                sstack.push(duration);
                flag = true;
            }
        }
        return a;
    }
}

