class Solution:
    def exclusiveTime(self, n: int, logs: List[str]) -> List[int]:
        functions = [0] * n
        stack = []

        old_time = 0
        for log in logs:
            pid, action, time = log.split(":")
            pid, time = int(pid), int(time)

            ad_time = time + (action == "end") # adjusted time

            if stack:
                peek = stack[-1]
                functions[peek] += ad_time - old_time

            if action == "start":
                stack.append(pid)
            elif action == "end":
                stack.pop()

            old_time = ad_time

        return functions
