var exclusiveTime = function (n, logs) {
    let stack = [];
    let res = new Array(n).fill(0);
    for (let content of logs) {
        let log = new Log(content);

        if (log.isStart) {
            stack.push(log);
        } else {
            let top = stack.pop();
            let interval = log.time - top.time + 1;
            res[log.id] += interval;
            if (stack.length) {
                res[stack[stack.length - 1].id] -= log.time - top.time + 1;
            }
        }
    }
    return res;
};

class Log {
    constructor(content) {
        let [id, event, time] = content.split(":");
        this.id = parseInt(id);
        this.isStart = event === "start";
        this.time = parseInt(time);
    }
}
