class Solution {
    public int[] exclusiveTime(int n, List<String> logs) {

        List<FunctionLog> logList = new ArrayList<>();
        for(String log : logs){
            String[] logData = log.split(":");
            int functionID = Integer.valueOf(logData[0]);
            int time = Integer.valueOf(logData[2]);
            boolean isStartTime = (logData[1].equals("start") ? true : false);

            logList.add(new FunctionLog(functionID, time, isStartTime));
        }

        int numFunction = n;
        int[] ans = new int[numFunction];

        int prevTime = 0;
        Stack<Integer> st = new Stack<>();
        for(int i=0; i<logList.size(); i++){
            if(!st.isEmpty()){
                ans[st.peek()] += logList.get(i).time - prevTime;
            }

            prevTime = logList.get(i).time;
            if(logList.get(i).isStartTime){
                st.push(logList.get(i).functionID);
            }else{
                ans[st.pop()]++;
                prevTime++;
            }
        }

        return ans;
    }

    private class FunctionLog{
        public int time;
        public int functionID;
        public boolean isStartTime;

        public FunctionLog(int functionID, int time, boolean isStartTime){
            this.functionID = functionID;
            this.time = time;
            this.isStartTime = isStartTime;
        }
    }
}
