class Solution {
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) {
        using T = pair<int,int>; // {id, start-time};
        stack<T> st;  
        vector<int> res(n, 0), toks(3);

        for (auto &s: logs) {
            toks = { stoi(s),
                    (s[s.find(':')+1]=='s')?0:1,
                    stoi(s.substr(s.rfind(':')+1)) };

            if (toks[1] == 0)   //"start"
                st.push({toks[0], toks[2] });
            else {
                int t = toks[2]-st.top().second +1;  
                res[st.top().first] += t;      // current function plus;
                st.pop();
                if (!st.empty())
                    res[st.top().first] -= t;   //previous function minus;
            }
        }

        return res;
    }
};

