class Solution {
    public int[] exclusiveTime(int n, List < String > logs) {


        // to store the logs, if its start -> push, else pop.
        Stack < String > stack = new Stack < String > ();
        int f1 = 0, f2 = 0; // store the function id
        String se1 = "", se2 = ""; // stores the start/end values
        int t1 = 0, t2 = 0; // stores the time.

        int[] arr = new int[n];

        // iterate over the logs
        for (String str: logs) {
            String[] split = str.split(":");
            if (se1.equals("")) { // if its first entry
                f1 = Integer.parseInt(split[0]);
                se1 = split[1];
                t1 = Integer.parseInt(split[2]);
                // System.out.println(f1 + " -- " + t1 + " -- " + se1);
                stack.push(str);
            } else { // rest all entries
                f2 = Integer.parseInt(split[0]);
                se2 = split[1];
                t2 = Integer.parseInt(split[2]);
                // System.out.println(f2 + " -- " + t2 + " -- " + se2);

                // if current is start, push into stack, and calculate the time for either old stack func id or previous func id.
                if (se2.equals("start")) {
                    if (se1.equals("start")) {// previous func id 
                        arr[f1] += t2 - t1;
                    } else if (se1.equals("end")) {
                        if (t2 != t1 && !stack.isEmpty()) { // time difference betwen start & end, means stack.peek() has run for that much time. old func id
                            // eg:
                            // 2
                            // ["0:start:0","0:start:2","0:end:5","1:start:7","1:end:7","0:end:8"]

                            // System.out.println("stack.peek(): " + stack.peek());
                            String[] temp = stack.peek().split(":");
                            int fTemp = Integer.parseInt(temp[0]);
                            String seTemp = temp[1];
                            int tTemp = Integer.parseInt(temp[2]);
                            // System.out.println("push: fTemp: " + fTemp + ", seTemp: " + seTemp  +", tTemp: " + tTemp);
                            arr[fTemp] += t2 - t1 - 1;
                        }
                    }
                    stack.push(str);
                } else if (se2.equals("end")) { // if current is end, then pop from stack and calculate the time of current func id.
                    String[] temp = stack.pop().split(":");
                    int fTemp = Integer.parseInt(temp[0]);
                    String seTemp = temp[1];
                    int tTemp = Integer.parseInt(temp[2]);
                     // System.out.println("pop: fTemp: " + fTemp + ", seTemp: " + seTemp  +", tTemp: " + tTemp);
                    if (se1.equals("start")) {
                        arr[f2] += t2 - t1 + 1;
                    } else if (se1.equals("end")) {
                        arr[f2] += t2 - t1;
                    }
                }
                // System.out.println("arr[" + 0 + "]: " + arr[0]);
                // System.out.println("arr[" + 1 + "]: " + arr[1]);


                // System.out.println("f1: " + f1 + ", se1: " + se1  +", t1: " + t1 + ", f2: " + f2 + ", se2: " + se2  +", t2: " + t2);
                // swap the logs, so that we have the data for prev & current
                f1 = f2;
                se1 = se2;
                t1 = t2;
            }
        }
        // for (int i = 0; i < n; i++) {
        //     System.out.println("arr[" + i + "] : " + arr[i]);
        // }

        return arr;
    }
}

