    def exclusiveTime(self, n: int, logs: List[str]) -> List[int]:
        last_start_time = 0
        stack = []
        hashmap = defaultdict(int)
        for log in logs:
            _id, _type, _time = log.split(':')
            _id, _time = int(_id), int(_time)
            if _type == "start":
                if len(stack):
                    hashmap[stack[-1]] += (_time - last_start_time)
                stack.append(_id)
                last_start_time = _time
            else:
                hashmap[stack[-1]] += (_time - last_start_time + 1)
                last_start_time = _time + 1
                stack.pop()
        return [hashmap[key] for key in range(n)]
