class Solution {
public:
    typedef struct {
        int id;
        int isStart;
        int ts;
    } log;

    vector<log> parseLogs(vector<string>& logs) {
        vector<log> L;
        for(string l:logs) {
            int id_end = l.find_first_of(':');
            int id = stoi(l.substr(0, id_end));
            int isStart = l[id_end+1] == 's' ? 1 : 0;
            int ts_start = isStart ? 6 + id_end + 1 : 4 + id_end + 1;
            int ts = stoi(l.substr(ts_start));
            L.push_back({id, isStart, ts});
        }
        return L;
    }

    vector<int> exclusiveTime(int n, vector<string>& logs) {
        vector<log> L = parseLogs(logs);
        vector<int> result(n,0);
        stack<pair<int, int>> unfinished;
        int spent_time_by_others = 0;
        for(log l:L) {
            if(l.isStart){
                unfinished.push({l.ts, spent_time_by_others});
            } else {
                pair<int,int> prev = unfinished.top();
                unfinished.pop();
                int time_spent = l.ts - prev.first + 1 - (spent_time_by_others - prev.second);
                result[l.id] += time_spent;
                spent_time_by_others += time_spent;
            }
        }
        return result;
    }
};
