class Solution {
    public int[] exclusiveTime(int n, List<String> logs) {
        Stack<Pair<Integer, Pair<Integer, Integer>>> processStack = new Stack<>();
        int[] processTime = new int[n];
        for(String log : logs) {
            int[] tokens = getLogTokens(log);
            int currTime = tokens[2];
            int pId = tokens[0];
            if (tokens[1] == 0) {                
                processStack.push(new Pair<>(pId, new Pair<>(currTime, 0)));
            } else {
                Pair<Integer, Pair<Integer, Integer>> pair = processStack.pop();
                int timeSpent = (currTime - pair.getValue().getKey() + 1 - pair.getValue().getValue());
                processTime[pair.getKey()] = processTime[pair.getKey()] + timeSpent;
                if (!processStack.isEmpty()) {
                    Pair<Integer, Pair<Integer, Integer>> topPair = processStack.pop();
                    topPair = new Pair<>(topPair.getKey(), new Pair<>(topPair.getValue().getKey(), topPair.getValue().getValue() + currTime - pair.getValue().getKey() + 1));      
                    processStack.push(topPair);
                }
            }
        }
        return processTime;
    }

