        String[] tokens = str.split(":");
        int[] tokensArray = new int[3];
        tokensArray[0] = Integer.parseInt(tokens[0]);
        tokensArray[1] = tokens[1].equals("start")? 0 : 1;
        tokensArray[2] = Integer.parseInt(tokens[2]);
        return tokensArray;
    }
}
