// Stack Solution
// runningTime = curLog.time - pairLog.time + 1;
// current job time + running time
// previous job time - running time
// Time complexity: O(N)
// Space complexity: O(N)
class Solution {
    public int[] exclusiveTime(int n, List<String> logs) {
        int[] res = new int[n];
        Deque<Log> stack = new ArrayDeque<>();
        for (String text : logs) {
            Log curLog = new Log(text);
            if (curLog.isStart) {
                stack.push(curLog);
            } else {
                Log pairLog = stack.pop();  // The corresponding start log.
                int runningTime = curLog.time - pairLog.time + 1;
                res[pairLog.id] += runningTime;  // current job time + running time
                if (!stack.isEmpty()) {
                    res[stack.peek().id] -= runningTime;  // previous job time - running time
				}
            }
        }
        return res;
    }
    class Log {
        int id;
        boolean isStart;
        int time;
        Log(String text) {
            String[] strs = text.split(":");
            id = Integer.valueOf(strs[0]);
            isStart = "start".equals(strs[1]);
            time = Integer.valueOf(strs[2]);
        }
    }
}
