class Solution {
    public int[] exclusiveTime(int n, List<String> logs) {
        int[] ans = new int[n];
        Stack<Function> order = new Stack<>();

        for(String log:logs){

            String[] logInfo = log.split(Function.SEPARATOR);

            if(logInfo[1].equals(Function.START)){
                order.push(new Function(logInfo));
            } else{
                Function ending = order.pop();

                int endingRunTime = Integer.parseInt(logInfo[2]) - ending.startTime + 1 - ending.idleTime;
                ans[ending.id] += endingRunTime;

                if(!order.isEmpty()){
                    Function topInOrder = order.peek();
                    topInOrder.idleTime += (endingRunTime+ending.idleTime);
                }
            }
        }
        return ans;
    }
