class Solution {

    static class FunctionCallEvent {
        int functionId;
        int startTime;

        FunctionCallEvent(int fid, int st) {
            functionId=fid;
            startTime=st;
        }

    }

    public int[] exclusiveTime(int n, List<String> logs) {

        int[] totalTime = new int[n];
        Deque<FunctionCallEvent> functionCallStack = new LinkedList<>();

        for(String log: logs) {
            String[] logInfo = log.split(":");

            int fid = Integer.parseInt(logInfo[0]);
            String eventType = logInfo[1];
            int timestamp = Integer.parseInt(logInfo[2]);

            switch(eventType) {
                case "start": {
                    if(!functionCallStack.isEmpty()) {

                        /*
                        Function which is currently at the top of stack will be paused and a new function is about to be executed. So total time taken by that function needs to be saved.
                        */

                        FunctionCallEvent e = functionCallStack.peek();
                        int timeTaken = timestamp - e.startTime;
                        totalTime[e.functionId] += timeTaken;

                        // e.startTime = -1; //optional; will be reset
                    }
                    functionCallStack.push(new FunctionCallEvent(fid,timestamp));
                    break;
                }
                case "end": {
                    /*
                    Compute how much time the stack top function took to execute
                    */
                    FunctionCallEvent e = functionCallStack.pop();
                    int timeTaken = timestamp - e.startTime + 1;
                    totalTime[e.functionId] += timeTaken;

                    if(!functionCallStack.isEmpty()) {
                        /*
                        This function will resume executing. So reset its startTime
                        */
                        functionCallStack.peek().startTime = timestamp+1;
                    }
                    break;
                }
                default: {}
            }
        }

        return totalTime;
    }
}
