class Solution {
    struct Event {
        bool isStart;
        int timestamp;
        int id;

        Event(bool isStart, int timestamp, int id): isStart(isStart), timestamp(timestamp), id(id) {}
    };

    string parseNext(string &log, int &ind) {
        if(log[ind] == ':') {
            ind++;
        }

        string result = "";
        while(ind < log.size() && log[ind] != ':') {
            result += log[ind++];
        }

        return result;
    }

    vector<Event> parseEvents(vector<string>& logs) {
        vector<Event> events;
        for(auto &log: logs) {
            int ind = 0;
            string id = parseNext(log, ind);
            string type = parseNext(log, ind);
            string timestamp = parseNext(log, ind);

            Event event = {
                type == "start",
                stoi(timestamp) + (type == "start" ? 0 : 1),
                stoi(id)
            };

            events.push_back(event);
        }

        return events;
    }

    vector<int> getExclusiveTimes(int n, vector<Event> &events) {
        stack<Event> eventStack;
        int lastTimestamp = 0;

        vector<int> exclusiveTimes(n, 0);

        for(auto &event: events) {
            if(event.isStart) {
                if(!eventStack.empty()) {
                    Event prevEvent = eventStack.top();
                    int timeSpent = event.timestamp - lastTimestamp;
                    exclusiveTimes[prevEvent.id] += timeSpent;
                }

                eventStack.push(event);
                lastTimestamp = event.timestamp;
            }
            else {
                int timeSpent = event.timestamp - lastTimestamp;
                exclusiveTimes[event.id] += timeSpent;
                eventStack.pop();
                lastTimestamp = event.timestamp;
            }
        }

        return exclusiveTimes;
    }
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) {
        vector<Event> events = parseEvents(logs);
        return getExclusiveTimes(n, events);
    }
};
