class Solution {
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) 
    {
        vector<int> res(n, 0);
        stack<pair<int, int>> st;

        //Iterate through given strings
        for(auto str : logs)
        {
            int i = 0, id = 0, time = 0;
            string op = "";

            //Get the function id
            while(i < str.size() && str[i] >= '0' && str[i] <= '9')
            {
                id = (id * 10) + (str[i] - '0');
                i++;
            }

            i++;

            //Get the type of operation performed
            while(str[i] != ':')
                op += str[i++];

            i++;

            //Get the timestamp
            while(i < str.size() && str[i] >= '0' && str[i] <= '9')
            {
                time = (time * 10) + (str[i] - '0');
                i++;
            }

            //If current operation is start, push to stack
            if(op == "start")
            {
                st.push({id, time});
            }
            //Else its an end operation
            else
            {
                auto prev = st.top();
                st.pop();

                int offsetTime = time - prev.second + 1;

                //Add offset time to corresponding function
                res[prev.first] += offsetTime;

                //If we have another function on stack, reduce the offset time from its running time
                if(!st.empty())
                {
                    res[st.top().first] -= offsetTime;
                }
            }
        }
        return res;
    }
};
