class Solution {
    int[] aux;
    int i = 0;
    public int[] exclusiveTime(int n, List<String> logs) {
        i = 0;
        aux = new int[n];
        while(i<logs.size()) {
            process(logs);
        }        
        return aux;
    }

    public int process(List<String> logs) {
        String[] first = logs.get(i++).split(":");
        int nested = 0;
        while(!logs.get(i).contains("end")) {
