    public int process(List<String> logs) {
        String[] first = logs.get(i++).split(":");
        int nested = 0;
        while(!logs.get(i).contains("end")) {
            nested += process(logs);
        }
        String[] last = logs.get(i++).split(":");
        int duration = Integer.parseInt(last[2]) - Integer.parseInt(first[2]) + 1;
        aux[Integer.parseInt(first[0])] += duration - nested;
        return duration;
    }
