class Solution {
    class Log {
        int id;
        int time;
        String op;

        Log(String log) {
            String[] result = log.split(":");
            this.id = Integer.parseInt(result[0]);
            this.op = result[1];
            this.time = Integer.parseInt(result[2]);
        }

        public boolean isStart() {
            return this.op.equals("start");
        }
    }

    public int[] exclusiveTime(int n, List<String> logs) {
        int[] output = new int[n];
        Stack<Log> stack = new Stack<>();
        int prevTime = 0;
        for(String log: logs) {
            Log cur = new Log(log);
            if (cur.isStart()) {
                if (!stack.empty()) {
                    output[stack.peek().id] += cur.time-prevTime;
                } 
                stack.push(cur);
                prevTime = cur.time;
            } else {
                stack.pop();
                output[cur.id] += cur.time-prevTime+1;
                prevTime = cur.time+1;
            }
        }
        return output;
    }
}
