var exclusiveTime = function(n, logs) {
    execTime = Array(n).fill(0);
    callStack = [];

    for(const log of logs) {
        [ pid, functionStatus, ts ] = log.split(':');

        if(functionStatus === 'start') {
            if(callStack.length) {
			    // storing the parent function which is calling the current function
                parentFunctionPid = callStack[callStack.length - 1][0];
            }
            else {
                parentFunctionPid = -1;
            }

            callStack.push([ pid, ts, parentFunctionPid ]);
        }
        else {
            [ childPid, childStartTs, parentPid ] = callStack.pop();

            fnExecTime = ts - childStartTs + 1;
            execTime[childPid] += fnExecTime;

		    // execution time of the child fuction subtracted from the parent function.
            if(parentPid !== -1) {
                execTime[parentPid] -= fnExecTime;
            }
        }
    }

    return execTime;
};
