            let splited=log.split(separator:":")
            let id=Int(splited[0]), time=Int(splited[2])
            if splited[1]=="start" {
                stack.append((id!,time!))
            }
            else {
                var (currFuncId, currFuncTime)=stack.removeLast()
                let runtime=time!-currFuncTime+1
                result[currFuncId]+=runtime //An important invariant of this algo is to maintain an up-to-the-moment cumulative runtime for each function in result
                if stack.count>0 { 
                    var (currFuncId, currFuncTime)=stack.last!   //The current func on top can resume its running, but adjusted by the duration for which it did not run
                    result[currFuncId]-=runtime
                }
            }
        }
        return result
    }
}
