class Solution {
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) {
        vector<int>     result(n,0);
        vector<int>     vID;
        int             prev_time = 0;
        for(string s : logs)
        {
            /* Parse Log */
            int     c_pos1    = s.find(':');
            int     c_pos2    = s.find_last_of(':');
            int     p_id      = stoi(s.substr(         0,  c_pos1)); 
            string  p_status  = s.substr(       c_pos1+1,  c_pos2-(c_pos1+1)); 
            int     curr_time = stoi(s.substr(  c_pos2+1)); 

            if( p_status =="start") {
                if( !vID.empty() ){
                    result[ vID.back() ] += curr_time - prev_time;
                }
                vID.push_back( p_id );
                prev_time = curr_time;                
            }
            if( p_status =="end") {                
                result[ p_id ] += curr_time - prev_time+1;;
                vID.pop_back();
                prev_time = curr_time+1;
            }
        }
        return result;
    }
};
