class Solution {
    public int candy(int[] ratings) {
        int n =  ratings.length;
        if(n <= 1)
            return n;
        int up = 0,down = 0;
        int prevSlope = 0;
        int candies = 0;

        for(int i=1;i<n;i++)
        {
            //calculate the slope
            //assign -ve +iv or zero for the decreasing increasing or plain slope
            //increasing slope = 1
            //decreasing slope = -1
            //plain slope = 0
            int current_Slope = ratings[i]>ratings[i-1]?1:
                                (ratings[i]<ratings[i-1]?-1:0);
            //divide the mountains on 3 conditions
            /*Condition 1:
              If we are getting a mountain like \/ i.e the slope is decreasing and then increasing,

            */

            /*Condition 2:
              If we are getting a mountain like \_ i.e the slope is decreasing and then plain,

            */

            /*Condition 3:
                                               _
              If we are getting mountain like /  i.e the slope is decreasing and then increasing,

            */
            if((prevSlope<0 && current_Slope>=0) || (prevSlope>0 && current_Slope==0))
            {
                candies = candies + sum(up) + sum(down) + Math.max(up,down);
                up=0;
                down=0;
            }
            if(current_Slope > 0)
                up++;
            else if(current_Slope < 0)
                down++;
            else
                candies++;
            prevSlope = current_Slope;
        }
        //add the candies that are there for the last mountain
        candies = candies + sum(up) + sum(down) + Math.max(up,down) + 1;
        return candies;
    }
    public int sum(int n)
    {
        return (n*(n+1))/2;
    }
}
