    int candy(vector<int>& ratings) {
        int n = ratings.size();
        vector<int> ans(n,1);
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> qe;
        for(int i = 0 ; i < n ; i++){
            qe.push({ratings[i],i});
