class Solution {
    public int candy(int[] ratings) {
        int n = ratings.length;
        int candies[] = new int[n];
        candies[0] = 1;
        // left traversal to cover the left neighbour condition
        for(int i = 1; i < n; i ++)
        {
            candies[i] = 1;
            if(ratings[i - 1] < ratings[i])
            {
                candies[i] = candies[i-1] + 1;
            }
        }

        // Right Traversal to cover the right neighbour
        for(int i = n - 1; i >=1 ; i--)
        {
            if(ratings[i - 1] > ratings[i] && candies[i-1] <= candies[i] )
            {
                candies[i - 1] = candies[i] + 1;
            }
        }
        int res = 0;
        for(int a: candies) res += a;
        return res;
    }
}
