class Solution {
public:
    int candy(vector<int> &ratings) {
        int sz=ratings.size();
        vector<int> scan0(sz);
        vector<int> scan1(sz);

        scan0[0] = 1; scan1[sz-1] = 1;
        for (int i=1; i<sz; i++) {
            scan0[i] = (ratings[i]>ratings[i-1] ? scan0[i-1]+1 : 1);
            scan1[sz-1-i] = (ratings[sz-1-i]>ratings[sz-i] ? scan1[sz-i]+1 : 1);
        }
        int sum = 0;
        for (int i=0; i<sz; i++) {
            sum += max(scan0[i], scan1[i]);
        }

        return sum;
    }
};

