class Solution {
public:
    int candy(vector<int>& r) {
        int n=r.size();vector<int> v(n,1);
        for(int i=1;i<n;i++){   int j=n-i-1;
            if(r[i]>r[i-1]) v[i]=max(v[i],v[i-1]+1);
            if(r[j]>r[j+1]) v[j]=max(v[j],v[j+1]+1);
        }return(accumulate(v.begin(),v.end(),0));
    }
};
