        int n = ratings.size();
        vector<int> left_to_right(n,1);
        vector<int> right_to_left(n,1);

        for(int i =1;i<n;i++){
            if(ratings[i]>ratings[i-1]){
                left_to_right[i] =left_to_right[i-1]+1;
            }
