class Solution {
public:
    int candy(vector<int>& a) {
        int indexOfLatestLocalMax = 0;
        int ans = 1;
        int latestLocalMaxCandies = 1;
        int currCandies=1;
        int offSetCandies;
        for(int i=1;i<a.size();i++) {
            if (a[i] >= a[i-1]) {
                currCandies = a[i] == a[i-1] ? 1 : currCandies + 1;
                ans += currCandies;
                latestLocalMaxCandies = currCandies;
                indexOfLatestLocalMax = i;
            } else {
                currCandies = 1;
                offSetCandies = i - indexOfLatestLocalMax;
                if (offSetCandies < latestLocalMaxCandies) {
                    offSetCandies -= 1;
                }
                ans += currCandies + offSetCandies;
            }
        }
        return ans;
    }
};
