class Solution {
public:
    int candy(vector<int>& a) {
        int n=a.size();
        vector<int> ans(n,1);
        if(n==1)
        {
            return 1; // if there is only one element then answer will always be 1.
        }
        for(int i=1;i<n;i++) // considering i-1 and i index from left to right
        {
            if(a[i]>a[i-1] and ans[i-1]>=ans[i])
            {
                ans[i]=ans[i-1]+1;
            }

            if(a[i-1]>a[i] and ans[i]>=a[i-1])
            {
                ans[i-1]=ans[i]+1;
            }
        }
        for(int i=n-2;i>=0;i--) //considering i and i+1 index from  right to left
        {
            if(a[i]>a[i+1] and ans[i+1]>=ans[i])
            {
                ans[i]=ans[i+1]+1;
            }
            if(a[i+1]>a[i] and ans[i]>=a[i+1])
            {
                ans[i+1]=ans[i]+1;
            }
        }
        int sum=0;
        for(auto x: ans)
        {
            sum+=x;
        }
        return sum;
    }
};

