        for(int i=1;i<n;i++){
            if(ratings[i]>ratings[i-1]){
                max=max+1;
                pre[i]=max;
            }
            else{
                max=1;
                pre[i]=max;
            }
        }

        vector<int>suff(n);
        max=1;
        suff[n-1]=max;
        for(int i=n-2;i>=0;i--){
            if(ratings[i]>ratings[i+1]){
                max=max+1;
                suff[i]=max;
            }
            else{
                max=1;
                suff[i]=max;
