def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:
	n = len(special)
	m = len(needs)

	def getCount(state, i):
		 (state >> 4 * (m - i - 1)) & ((1 << 4) - 1)

	def setCount(state, count):
		return (state << 4) | count

	@cache
	def helper(state):
		if not state:
			return 0
		res = sum(price[j] * getCount(state, j) for j in range(m))
        for i in range(n):
			next_state = 0
            for j in range(m):
				count = getCount(state, j)
                if count < special[i][j]:
					break
				next_state = setCount(next_state, count - special[i][j])
			else:
				res = min(res, special[i][-1] + helper(next_state))
		return res

	state = 0
    for i, num in enumerate(needs): 
		state = setCount(state, num)
	return helper(state)
