class Solution:
    def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:

        def helper(res, specials, needs):
            if sum(needs) == 0:
                return res

            if not specials:
                return res + sum([price[i]*needs[i] for i in range(len(needs))])

            special = specials[0]
            for i in range(len(needs)):
                if special[i] > needs[i]:
                    return helper(res, specials[1:], needs)

            return min(helper(res, specials[1:], needs), helper(res+special[-1], specials, [needs[i]-special[i] for i in range(len(needs))]))

        return helper(0, special, needs)

