#include <vector>
#include <limits>
#include <numeric>
#include <unordered_map>

using namespace std;

const int INF = numeric_limits<int>::max();
const int MAX_N = 106;

int dp[MAX_N + 1][117649];

class Solution {
private:
    int encode(vector<int>& needs) {
        int res = 0;
        for (int num : needs) {
            res = res * 7 + num;
        }
        return res;
    }
    int minPrice(vector<vector<int>>& offers, int n, vector<int>& needs, int tot) {
        if (tot == 0) {
            return 0;
        }

        if (n == 0) {
            return INF;
        }

        int key = encode(needs);
        if (dp[n][key] != -1) {
            return dp[n][key];
        }

        int res1 = minPrice(offers, n - 1, needs, tot);
        int res2 = INF;

        bool possible = true;
        int items = needs.size();
        for (int i = 0; i < items; ++i) {
            if (offers[n - 1][i] > needs[i]) {
                possible = false;
                break;
            }
        }

        if (possible) {
            for (int i = 0; i < items; ++i) {
                needs[i] -= offers[n - 1][i];
                tot -= offers[n - 1][i];
            }
            res2 = minPrice(offers, n, needs, tot);
            if (res2 != INF) {
                res2 += offers[n - 1][items];
            }
            for (int i = 0; i < items; ++i) {
                needs[i] += offers[n - 1][i];
                tot += offers[n - 1][i];
            }
        }

        int res = min(res1, res2);
        dp[n][key] = res;
        return res;
    }
public:
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        int n = price.size();
        for (int i = 0; i < n; ++i) {
            vector<int> offer(n + 1, 0);
            offer[i] = 1;
            offer[n] = price[i];
            special.push_back(offer);
        }

        int maxK = encode(needs);

        int numOffer = special.size();
        for (int i = 0; i <= numOffer; ++i) {
            fill(dp[i], dp[i] + maxK, -1);
        }
        int tot = accumulate(needs.begin(), needs.end(), 0);
        return minPrice(special, numOffer, needs, tot);
    }
};

#if DEBUG
int main(int argc, char** argv) {
    return 0;
}
#endif

