class Solution:
    def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:
        @cache
        def dfs(i, needs):
            res = sum(p*n for p,n in zip(price, needs))
            if i == len(special):
                return res
            cur = 0
            while min(needs)>=0:
                res = min(res, cur + dfs(i+1, tuple(needs)))
                needs = [a-b for a,b in zip(needs, special[i])]
                cur += special[i][-1]
            return res
        return dfs(0, tuple(needs))

